mapper.select:
SELECT

FROM x_code_user a

WHERE a.id = #{id}

SELECT
count(*) as count
FROM x_code_user a


    flag=#{flag}

        AND a.username like CONCAT('%',#{username},'%')


        AND t.wechart = #{wechart}


        AND a.phone = #{phone}


        AND a.role_code = #{roleCode}

SELECT

FROM
(SELECT id from x_code_user t

    flag=#{flag}

        AND a.username like CONCAT('%',#{username},'%')


        AND t.wechart = #{wechart}


        AND t.phone = #{phone}


        AND a.role_code = #{roleCode}




        ORDER BY t.UPDATE_TIME desc


limit #{startIndex},#{pageSize}) b inner join
x_code_user a on b.id=a.id

SELECT

(SELECT id from x_code_user t

    flag=#{flag}

        AND a.username like CONCAT('%',#{username},'%')


        AND t.wechart = #{wechart}


        AND t.phone = #{phone}


        AND a.role_code = #{roleCode}




        ORDER BY t.UPDATE_TIME desc


) b inner join x_code_user a on b.id=a.id

SELECT

FROM
(SELECT id from x_code_user t

    flag=#{flag}

        AND a.username like CONCAT('%',#{username},'%')


        AND t.password = #{password}


        AND t.phone = #{phone}


        AND a.role_code = #{roleCode}




        ORDER BY t.UPDATE_TIME desc


limit 1)b inner join x_code_user a on b.id=a.id

mapper.update:
UPDATE x_code_user SET
flag = -1
	WHERE id = #{id}
